import React, { useState } from 'react';
import About from './About';
import Contact from './Contact';
import './Content.css';

// Sample phone data
const phones = [
  {
    image: 'data:image/jpeg;base64,', // Replace with your image link
    name: 'iPhone 14',
    description: '6.1-inch display, A15 Bionic, 12MP dual camera, 5G support.',
  },
  {
    image: 'data:image/jpeg;base64,', // Replace with your image link
    name: 'Realme 9 Pro',
    description: '6.6-inch display, Snapdragon 695, 64MP triple camera, 5G support.',
  },
  {
    image: 'data:image/jpeg;base64,', // Replace with your image link
    name: 'Samsung Galaxy S23 Ultra',
    description: '6.8-inch display, 200MP quad camera, Snapdragon 8 Gen 2, S Pen support.',
  },
];

const Content = ({ section }) => {
  const [showPhones, setShowPhones] = useState(false);

  const handleExploreClick = () => {
    setShowPhones(true); // Show phone details when Explore More is clicked
  };

  const renderPhones = () => {
    return (
      <section className="phone-details">
        <h2>Phone Details</h2>
        <div className="phone-cards">
          {phones.map((phone, index) => (
            <div className="phone-card" key={index}>
              <img src={phone.image} alt={phone.name} className="phone-image" />
              <h3>{phone.name}</h3>
              <p>{phone.description}</p>
            </div>
          ))}
        </div>
      </section>
    );
  };

  const renderSection = () => {
    if (section === 'about') {
      return <About />;
    } else if (section === 'contact') {
      return <Contact />;
    } else if (showPhones) {
      return renderPhones(); // Render phone cards
    } else {
      return (
        <section className="welcome-section">
          <h1>Welcome to Mobile Store</h1>
          <p>Explore the exciting Mobiles of your choice</p>
          <button className="explore-button" onClick={handleExploreClick}>
            Explore More
          </button>
        </section>
      );
    }
  };

  return <div className="content">{renderSection()}</div>;
};

export default Content;
